import React, { useState } from 'react';
import DragAndDrop from './DragDrop'; // Import the drag and drop component

const Popup = ({ isPopupVisible, setPopupVisible, recipe }) => {
  const [activeTab, setActiveTab] = useState("recipe");

  const hidePopup = () => {
    setPopupVisible(false);
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  if (!recipe) return null; // Don't render popup if no recipe is provided

  return (
    <div>
      <div className={`popup-overlay ${isPopupVisible ? "active" : ""}`} onClick={hidePopup}>
        <div className="popup-content" onClick={(e) => e.stopPropagation()}>
          <button className="popup-close" onClick={hidePopup}>
            &times;
          </button>

          {/* Tab buttons */}
          <div className="tabs">
            <button
              className={`tab-button ${activeTab === "recipe" ? "active" : ""}`}
              onClick={() => handleTabClick("recipe")}
            >
              Recipe
            </button>
            <button
              className={`tab-button ${activeTab === "animation" ? "active" : ""}`}
              onClick={() => handleTabClick("animation")}
            >
              Animation
            </button>
          </div>

          {/* Recipe content */}
          {activeTab === "recipe" && recipe && (
            <div className="recipe-content">
              <h2>{recipe.title}</h2>
              {recipe.ingredients && recipe.ingredients.length > 0 && (
                <>
                  <p><strong>Ingredients:</strong></p>
                  <ul>
                    {recipe.ingredients.map((ingredient, index) => (
                      <li key={index}>{ingredient}</li>
                    ))}
                  </ul>
                </>
              )}
              {recipe.instructions && recipe.instructions.length > 0 && (
                <>
                  <p><strong>Instructions:</strong></p>
                  <ol>
                    {recipe.instructions.map((instruction, index) => (
                      <li key={index}>{instruction}</li>
                    ))}
                  </ol>
                </>
              )}
            </div>
          )}

          {/* Animation content */}
          {activeTab === "animation" && (
            <div className="animation-content">
              <DragAndDrop />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Popup;
