import "./popup.css";

const Popup = ({ isPopupVisible, setPopupVisible }) => {
  const hidePopup = () => {
    setPopupVisible(false);
  };

  return (
    <div>
      <div className={`popup-overlay ${isPopupVisible ? "active" : ""}`} onClick={hidePopup}>
        <div className="popup-content" onClick={(e) => e.stopPropagation()}>
          <button className="popup-close" onClick={hidePopup}>
            &times;
          </button>

          {/* Recipe content */}
          {isPopupVisible && (
            <div className="recipe-content">
              <h2>Delicious Pancakes Recipe</h2>
              <p><strong>Ingredients:</strong></p>
              <ul>
                <li>1 cup all-purpose flour</li>
                <li>2 tablespoons sugar</li>
                <li>1 tablespoon baking powder</li>
                <li>1/2 teaspoon salt</li>
                <li>1 cup milk</li>
                <li>1 egg</li>
                <li>2 tablespoons melted butter</li>
                <li>1 teaspoon vanilla extract</li>
              </ul>

              <p><strong>Instructions:</strong></p>
              <ol>
                <li>In a bowl, whisk together the dry ingredients: flour, sugar, baking powder, and salt.</li>
                <li>In another bowl, whisk together the wet ingredients: milk, egg, butter, and vanilla extract.</li>
                <li>Pour the wet ingredients into the dry ingredients and stir until just combined.</li>
                <li>Heat a non-stick skillet over medium heat. Lightly grease it with butter or cooking spray.</li>
                <li>Pour 1/4 cup of batter onto the skillet for each pancake. Cook until bubbles form on the surface, then flip and cook until golden brown.</li>
                <li>Serve with syrup, fruit, or your favorite toppings.</li>
              </ol>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Popup;
