import React, { useState } from 'react';
import "./popup.css";

const DragAndDrop = () => {
  const [droppedImages, setDroppedImages] = useState([]);

  const handleDrop = (e) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    const images = Array.from(files).map((file) => URL.createObjectURL(file));
    setDroppedImages((prevImages) => [...prevImages, ...images]);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  return (
    <div
      className="animation-placeholder"
      onDrop={handleDrop}
      onDragOver={handleDragOver}
    >
      <h3>Drag and Drop Images Here</h3>
      <div className="dropped-images">
        {droppedImages.map((image, index) => (
          <img key={index} src={image} alt={`dropped-logo-${index}`} className="dropped-image" />
        ))}
      </div>
    </div>
  );
};

const Popup = ({ isPopupVisible, setPopupVisible, recipe }) => {
  const [activeTab, setActiveTab] = useState("recipe"); // Default to "recipe" tab

  const hidePopup = () => {
    setPopupVisible(false);
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  if (!recipe) return null; // Don't render popup if no recipe is provided

  return (
    <div>
      <div className={`popup-overlay ${isPopupVisible ? "active" : ""}`} onClick={hidePopup}>
        <div className="popup-content" onClick={(e) => e.stopPropagation()}>
          <button className="popup-close" onClick={hidePopup}>
            &times;
          </button>

          {/* Tab buttons */}
          <div className="tabs">
            <button
              className={`tab-button ${activeTab === "recipe" ? "active" : ""}`}
              onClick={() => handleTabClick("recipe")}
            >
              Recipe
            </button>
            <button
              className={`tab-button ${activeTab === "animation" ? "active" : ""}`}
              onClick={() => handleTabClick("animation")}
            >
              Animation
            </button>
          </div>

          {/* Recipe content */}
          {activeTab === "recipe" && recipe && (
            <div className="recipe-content">
              <h2>{recipe.title}</h2>
              <p><strong>Ingredients:</strong></p>
              <ul>
                {recipe.ingredients.map((ingredient, index) => (
                  <li key={index}>{ingredient}</li>
                ))}
              </ul>
              <p><strong>Instructions:</strong></p>
              <ol>
                {recipe.instructions.map((instruction, index) => (
                  <li key={index}>{instruction}</li>
                ))}
              </ol>
            </div>
          )}

          {/* Animation content */}
          {activeTab === "animation" && (
            <div className="animation-content">
              <DragAndDrop />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Popup;
