import React, { useState } from "react";
import "./popup.css";

const Popup = () => {
  // State to manage the popup visibility
  const [isPopupVisible, setPopupVisible] = useState(false);

  // Function to show the popup
  const showPopup = () => {
    setPopupVisible(true);
  };

  // Function to hide the popup
  const hidePopup = () => {
    setPopupVisible(false);
  };

  return (
    <div>
      {/* Trigger Button to show the popup */}
      <button onClick={showPopup}>Show Popup</button>

      {/* Popup Overlay (with conditional visibility based on state) */}
      <div className={`popup-overlay ${isPopupVisible ? "active" : ""}`} onClick={hidePopup}>
        {/* Popup Content */}
        <div className="popup-content" onClick={(e) => e.stopPropagation()}>
          <h2>Welcome to the Popup!</h2>
          <p>This is a full-screen popup that appears when you click the button.</p>
          <button className="popup-close" onClick={hidePopup}>
            Close Popup
          </button>
        </div>
      </div>
    </div>
  );
};

export default Popup;
