import "./navbar.css"
import { Logo } from "../../assets/Logo.svg";
import { Link, useMatch, useNavigate, useResolvedPath } from "react-router-dom";
import { useContext, useState,useEffect } from "react";
import "./navbar.module.css";

export default function Navbar() {
  
  //const context = useContext(AuthContext);

  //const user = context.currentUser;

  const navigate = useNavigate();

  const [hamburguerOpen, setHamburguerOpen] = useState(false);
  

  const toggleHamburguer = () => {
    setHamburguerOpen(!hamburguerOpen);
  };

  const [isMobile, setMobile] = useState(window.innerWidth <= 735);

  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth <= 735;
      setMobile(mobile);
    };
  
    window.addEventListener('resize', handleResize);
  
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [isMobile, setMobile]);

  const type = localStorage.getItem("type");


  return (
    <div>
      <nav className={"nav"}>
        <div className="logo" onClick={() => navigate("/profile")}>
          
        </div>
        <div className={"links"}>
          <div className={"cruz"} onClick={toggleHamburguer}>
          </div> 
          {type === "business" ? (
            <>
              <CustomLink to={"/jobsbusiness"}>Empregos</CustomLink>
              <CustomLink to={"/workers"}>Propostas de Trabalho</CustomLink>
              <CustomLink to={"/messages"}>Mensagens</CustomLink>
            </>
          ) : (
            <>
              <CustomLink to={"/jobs"}>Ofertas de Emprego</CustomLink>
              <CustomLink to={"/workers"}>Propostas de Trabalho</CustomLink>
              <CustomLink to={"/proposals"}>√Årea Pessoal</CustomLink>
              <CustomLink to={"/messages"}>Mensagens</CustomLink>
            </>
          )}
        </div>
      </nav>
      <style jsx>{`

        .links {
          display: flex;
          flex-direction: row;
          height: 100%;
          align-items: center;
          justify-content: space-around;
        }
          
        @media screen and (max-width: 735px){
          .links{
            /*display: flex;*/
            display: ${hamburguerOpen ? "flex" : "none"};
            flex-direction: column;
            justify-content: center;  
            align-items: center;
            gap: 3rem;
            width: 100%;
            height: 100vh;
            background-color: #51596C;
            position: absolute;
            top: 0;
            left: 0;
            z-index: 1;
          }

          .links a{
            color: ${hamburguerOpen ? "#ffffff" : "#000000"};
            font-size: 1.5rem;
          }

          .cruz{
            display: ${hamburguerOpen ? "block" : "none"};
            position: relative;
            height: auto;
            z-index: 10;
            margin-top: -27vh;
            margin-bottom: 25%;
            margin-right: -75vw;
            cursor:pointer;
        }
        `}</style>
    </div>
  );
}

function CustomLink({ to, children, ...props }) {
  const resolvedPath = useResolvedPath(to);
  const isActive = useMatch({ path: resolvedPath.pathname });
  return (
    <li className={isActive ? styles.activeLink : styles.link}>
      <Link to={to} {...props}>
        {children}
      </Link>
    </li>
  );
}
