import { useNavigate } from "react-router-dom";
import Navbar from "../../components/Navbar/navbar";
import Button from "../../components/Button/button";
import ovo from "../../assets/imagePGI.png"; // The egg image
import React from "react";
import "./learn.css";

// Recursive Tree Component (Horizontal Layout with Larger Nodes)
const TreeNode = ({ node, x = 0, y = 0, parentX = null, parentY = null }) => {
  const childXOffset = 200; // Space between levels
  const childYOffset = 150; // Space between siblings

  return (
    <div>
      {/* Render Connection Lines */}
      {parentX !== null && parentY !== null && (
        <svg
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            overflow: "visible",
            zIndex: -1, // Ensure it's beneath the nodes but still visible
          }}
        >
          <line
            x1={parentX + 50} // Start point for the line (center of parent)
            y1={parentY + 50}
            x2={x + 50} // End point for the line (center of child)
            y2={y + 50}
            stroke="black"
            strokeWidth="2"
          />
        </svg>
      )}

      {/* Render the current node (egg image) */}
      <div
        style={{
          position: "absolute",
          left: x,
          top: y,
          width: "100px", // Increased width
          height: "100px", // Increased height
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          border: "1px solid #ffca28",
        }}
      >
        <img
          src={ovo} // Use the image for the node
          alt="Egg"
          style={{
            width: "80px", // Increased image size
            height: "80px", // Increased image size
            objectFit: "cover",
          }}
        />
      </div>

      {/* Render child nodes recursively */}
      {node.children &&
        node.children.map((child, index) => (
          <TreeNode
            key={index}
            node={child}
            x={x + childXOffset}
            y={ y + index * childYOffset - (node.children.length - 1) * (childYOffset / 2) }
            parentX={x}
            parentY={y}
          />
        ))}
    </div>
  );
};

// Main Learn Component
export default function Learn() {

  // Tree data for the egg tree
  const treeData = {
    children: [
      {
        children: [
          { children: [] },
          { children: [] },
        ],
      },
      {
        children: [],
      },
    ],
  };

  return (
    <>
      <Navbar />
      <div className="homepage">
        <div
          style={{
            position: "relative",
            height: "600px",
            width: "1000px",
            margin: "50px auto",
          }}
        >
          <TreeNode node={treeData} x={0} y={250}/>
        </div>
      </div>
    </>
  );
}
