import React, { useState } from "react";
import "./popup.css";

const Popup = ({ isPopupVisible, setPopupVisible, recipe }) => {
  const [activeTab, setActiveTab] = useState("ingredients"); // Track the active tab

  if (!recipe) return null; // Don't render popup if no recipe is provided

  const hidePopup = () => {
    setPopupVisible(false);
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab); // Change the active tab
  };

  return (
    <div>
      <div className={`popup-overlay ${isPopupVisible ? "active" : ""}`} onClick={hidePopup}>
        <div className="popup-content" onClick={(e) => e.stopPropagation()}>
          <button className="popup-close" onClick={hidePopup}>
            &times;
          </button>

          {/* Recipe content */}
          <h2>{recipe.title}</h2>

          {/* Tab Buttons */}
          <div className="tabs">
            <button
              className={`tab-button ${activeTab === "ingredients" ? "active" : ""}`}
              onClick={() => handleTabChange("ingredients")}
            >
              Ingredients
            </button>
            <button
              className={`tab-button ${activeTab === "instructions" ? "active" : ""}`}
              onClick={() => handleTabChange("instructions")}
            >
              Instructions
            </button>
          </div>

          {/* Render the active tab content */}
          {isPopupVisible && recipe && activeTab === "ingredients" && (
            <div className="recipe-content">
              <p><strong>Ingredients:</strong></p>
              <ul>
                {recipe.ingredients.map((ingredient, index) => (
                  <li key={index}>{ingredient}</li>
                ))}
              </ul>
            </div>
          )}

          {isPopupVisible && recipe && activeTab === "instructions" && (
            <div className="recipe-content">
              <p><strong>Instructions:</strong></p>
              <ol>
                {recipe.instructions.map((instruction, index) => (
                  <li key={index}>{instruction}</li>
                ))}
              </ol>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Popup;
