// App.js
import React, { useState } from 'react';
import { useDrag, useDrop } from 'react-dnd';

// Ball Component
const Ball = ({ id, imageUrl }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'BALL',
    item: { id, imageUrl },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  return (
    <img
      ref={drag}
      src={imageUrl}
      alt={`ball-${id}`}
      style={{
        width: '100px',
        height: '100px',
        opacity: isDragging ? 0.5 : 1,
        cursor: 'move',
      }}
    />
  );
};

// Frame Component
const Frame = ({ balls, setBalls }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'BALL',
    drop: (item) => {
      // Update the balls array with new positions when dropped
      const newBall = {
        ...item,
        left: Math.random() * 300, // Random position in the frame
        top: Math.random() * 300,
      };
      setBalls((prevBalls) => [...prevBalls, newBall]);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  return (
    <div
      ref={drop}
      style={{
        position: 'relative',
        width: '400px',
        height: '400px',
        border: '2px solid black',
        backgroundImage: 'url(/path/to/your/frame-image.png)', // Optional frame image
        backgroundSize: 'cover',
        marginTop: '20px',
      }}
    >
      {balls.map((ball, index) => (
        <img
          key={index}
          src={ball.imageUrl}
          alt={`ball-${ball.id}`}
          style={{
            position: 'absolute',
            top: ball.top,
            left: ball.left,
            width: '100px',
            height: '100px',
          }}
        />
      ))}
    </div>
  );
};

// Main App Component
const App = () => {
  const [balls, setBalls] = useState([]);
  const ballImages = [
    '/path/to/ball1.png',
    '/path/to/ball2.png',
    '/path/to/ball3.png',
    // Add your ball images here
  ];

  return (
    <div style={{ padding: '20px' }}>
      <h1>Drag Balls into the Frame</h1>
      <div style={{ display: 'flex', gap: '10px' }}>
        {ballImages.map((imageUrl, index) => (
          <Ball key={index} id={index} imageUrl={imageUrl} />
        ))}
      </div>
      <Frame balls={balls} setBalls={setBalls} />
    </div>
  );
};

export default App;
